 export  class Home extends Component {

    render() {
        const { data } = this.props;
        console.log ('DATA', data)
        return <Tab panes={panes({ data })}  />;
      }




}

const panes = (props)=> {

const {data} = props;

return [
{
menuItem : 'Unanswered', render: () =>  (
    <Tab.Pane>
     <Card.Group></Card.Group>   
     <CardDetails answeredQuestions = {data.answeredQuestions}/>
    </Tab.Pane>
     )
},

{
menuItem : 'Answered', render: () =>  <Tab.Pane>Answered</Tab.Pane>
}  
];

}
function mapStateToProps({ authedUser, users, questions }) {
    const answeredIds = Object.keys(users[authedUser].answers);

    const unansweredQuestions = Object.values(questions)
      .filter(question => !answeredIds.includes(question.id))
      .sort((a, b) => b.timestamp - a.timestamp);

   const answeredQuestions = Object.values(questions)
   .filter((question) => {
    return Object.keys(users[authedUser].answers).find(
    (userQuestion) => userQuestion === question.id)})
    .sort((a, b) => b.timestamp - a.timestamp);

    console.log('answeredQuestions', answeredQuestions)
    console.log('USERS', users)
    return {
     users,
      data: {
        answeredQuestions,
        unansweredQuestions,
       // answeredQuestions
      }
    };
  }

  export default connect(mapStateToProps)(Home);
