import {RECEIVE_QUESTIONS, ADD_QUESTION} from '../actions/questions'
import _DATA from '../utils/_DATA'
export default function questions(state = {}, action){


switch(action.type){

   case RECEIVE_QUESTIONS : {
    return {
    ...state,
    ...action.questions
    }
}

case ADD_QUESTION : {

        const { question } = action;
        return {

          ...state,
          [question.id]: question
        };
    }
    default:
        return state
}



}
