import { render } from '@testing-library/react'
import React, { Component } from 'react'
import { Dropdown, Container, Header } from 'semantic-ui-react'

class Login extends Component {
render(){
 return (

<div>


</div>




 )




}

}

const LoginContainer = ({ children })  => (
    <Container style={{ margin: 20 }}>
    <Header as="h3" block attached="top" textAlign="center">
      <Header.Content>Welcome to the Would You Rather App!</Header.Content>
      <Header.Subheader>Please sign in to continue</Header.Subheader>
    </Header>
     {children}
    </Container>
  );


  const LoginGrid= ({ image, form, loading }) => (
    <div>
      <Grid padded textAlign="center">
        <Grid.Row className="login">
          <Grid.Column width={16}>
            {loading === true && (
              <Dimmer active inverted>
                <Loader inverted content="Loading" />
              </Dimmer>
            )}
            {image}
            <br />
            {form}
          </Grid.Column>
        </Grid.Row>
      </Grid>
    </div>
  );
export default Login
